  var zero2 = day < 10 ? '0' : '';
  var zero3 = hours < 10 ? '0' : '';
  var zero4 = minutes < 10 ? '0' : '';
  return "".concat(zero1).concat(month, "-").concat(zero2).concat(day, " ").concat(zero3).concat(hours, ":").concat(zero4).concat(minutes);
} //YYYY-MM-dd hh:mm:ss


