import Vue from 'vue'
import Router from 'vue-router'
import store from '../../vuex/store'

Vue.use(Router);

const vm = new Vue({
    store
});

const router = new Router({
    routes: [
        /**
         * 后台主页
         */
        {
            path: '/',
            redirect: '/project_type/index',
        },

        /**
         * 登录
         */
        // {
        //     path: '/login',
        //     name: 'login',
        //     component:(resolve) => require(['@/views/login/Login.vue'],resolve),
        // },

        /**
         *  前台主页
         */
        /****************  一张图  *****************/
        {
            path: '/index',
            name:'map',
            component:(resolve) => require(['./views/map/MapIndex.vue'],resolve),
        },

        {
            path: '/front',
            name: 'frontIndex',
            component:(resolve) => require(['./views/main/Index.vue'],resolve),
            children:[
                /****************  首页  *****************/
                {
                    path: '/home',
                    name:'home',
                    component:(resolve) => require(['./views/home/Home.vue'],resolve),
                },
                /****************  工程管理  *****************/
                {
                    path: '/project_type/index',
                    name:'',
                    component:(resolve) => require(['./views/project/pump/PumpIndex.vue'],resolve),

                },
                {
                    path: '/project_type/detail/:id',
                    name:'projectDetail',
                    component:(resolve) => require(['./views/project/pump/PumpDetail.vue'],resolve),
                },
                /****************  运维管理  *****************/
                {
                    path: '/mobile/inspect',        //移动巡检
                    name:'mobileInspect',
                    component:(resolve) => require(['./views/oper/mobile/InspectIndex.vue'],resolve),
                    redirect: '/inspect/plan',
                    children:[
                        {
                            path: '/inspect/plan',
                            name:'inspectPlan',
                            component:(resolve) => require(['./views/oper/mobile/InspectPlan.vue'],resolve),
                        },
                        {
                            path: '/task/index',
                            name:'taskIndex',
                            component:(resolve) => require(['./views/oper/mobile/TaskIndex.vue'],resolve),
                        },
                    ]
                },
                /****************  用水户管理  *****************/
                {
                    path: '/water_user/index',
                    name:'waterUserIndex',
                    component:(resolve) => require(['./views/wateruser/WaterUserIndex.vue'],resolve),

                },
                {
                    path: '/water_user/detail/:id',
                    name:'wateruserDetail',
                    component:(resolve) => require(['./views/wateruser/WaterUserDetail.vue'],resolve),
                },
                /**************  运维管理  ****************/
                {   //阈值管理
                    path: '/threshold/index',
                    name:'thresholdIndex',
                    component:(resolve) => require(['./views/oper/threshold/ThresholdIndex.vue'],resolve),
                },
                {   //设备管理
                    path: '/equip/index',
                    name:'EquipIndex',
                    component:(resolve) => require(['./views/oper/equip/EquipIndex.vue'],resolve),
                },
                {   //报警管理
                    path: '/alarm/index',
                    name:'alarmIndex',
                    component:(resolve) => require(['./views/oper/alarm/AlarmIndex.vue'],resolve),
                    children:[
                        {
                            path: '/exceedlimit/index',    //越限报警
                            name:'exceedlimitIndex',
                            component:(resolve) => require(['./views/oper/alarm/ExceedLimitIndex.vue'],resolve),
                        },
                        {
                            path: '/power/index',    //供电报警
                            name:'powerIndex',
                            component:(resolve) => require(['./views/oper/alarm/PowerIndex.vue'],resolve),
                        },
                        {
                            path: '/communication/index',    //通讯报警
                            name:'communicationIndex',
                            component:(resolve) => require(['./views/oper/alarm/CommunicationIndex.vue'],resolve),
                        },
                        {    //越限报警维护记录
                            path: '/maintainrecor/index/:id',
                            name:'maintainrecordIndex',
                            component:(resolve) => require(['./views/oper/alarm/MaintainRecordIndex.vue'],resolve),
                        },
                    ]
                },
                {   //交接班管理
                    path: '/change/index',
                    name:'changeIndex',
                    component:(resolve) => require(['./views/oper/change/ChangeIndex.vue'],resolve),
                    children:[
                        {
                            path: '/change/shift',
                            name:'changeShift',
                            component:(resolve) => require(['./views/oper/change/ChangeShift.vue'],resolve),
                        },
                        {
                            path: '/hand/over/duty',   //交班
                            name:'HandOverDuty',
                            component:(resolve) => require(['./views/oper/change/HandOverDuty.vue'],resolve),
                        },
                        {
                            path: '/take/over/duty',   //接班
                            name:'TakeOverDuty',
                            component:(resolve) => require(['./views/oper/change/TakeOverDuty.vue'],resolve),
                        }
                    ]
                },
                {   //维修管理
                    path: '/repair/index',
                    name:'repairIndex',
                    component:(resolve) => require(['./views/oper/repair/RepairIndex.vue'],resolve),
                    children:[
                        {
                            path: '/record/index',
                            name:'recordIndex',
                            component:(resolve) => require(['./views/oper/repair/RecordIndex.vue'],resolve),
                        },
                        {
                            path: '/repair/order',   //交班
                            name:'repairOrder',
                            component:(resolve) => require(['./views/oper/repair/RepairOrder.vue'],resolve),
                        },
                    ]
                },
                {   //common
                    path: '/common/index/:id',
                    name:'alarmIndex',
                    component:(resolve) => require(['./views/common/commonIndex.vue'],resolve),
                },
                /**************  水费管理  ****************/
                {   //水费管理
                    path: '/rent/index',
                    name:'rentIndex',
                    component:(resolve) => require(['./views/waterrent/rent/RentIndex.vue'],resolve),
                },
                {   //水费补偿
                    path: '/compensate/index',
                    name:'compensateIndex',
                    component:(resolve) => require(['./views/waterrent/compensate/CompensateIndex.vue'],resolve),
                },
                {   //网点管理
                    path: '/networks/index',
                    name:'networksIndex',
                    component:(resolve) => require(['./views/waterrent/networks/NetworksIndex.vue'],resolve),
                },
                {   //发票管理
                    path: '/invoice/index',
                    name:'invoiceIndex',
                    component:(resolve) => require(['./views/waterrent/invoice/InvoiceIndex.vue'],resolve),
                },
                {   //账单管理
                    path: '/bill/index',
                    name:'billIndex',
                    component:(resolve) => require(['./views/waterrent/bill/BillIndex.vue'],resolve),
                },
                /*************************** 监测管理  *****************************/
                {   //水质监控
                    path: '/quality/index',
                    name:'/qualityIndex',
                    component:(resolve) => require(['./views/monitoring/waterquality/QualityIndex.vue'],resolve),
                    children:[
                        {
                            path: '/waterquality/index',    //在线监测
                            name:'waterQualityIndex',
                            component:(resolve) => require(['./views/monitoring/waterquality/WaterQualityIndex.vue'],resolve),
                        },
                        /*{
                            path: '',   //水质检测
                            name:'',
                            component:(resolve) => require([''],resolve),
                        },*/
                        {
                            path: '/quality/statistics',   //监测统计
                            name:'qualityStatistics',
                            component:(resolve) => require(['./views/monitoring/waterquality/QualityStatistics.vue'],resolve),
                        }
                    ]
                },
                {   //视频监控
                    path: '/vidio/index',
                    name:'vidioIndex',
                    component:(resolve) => require(['./views/monitoring/vidio/VidioIndex.vue'],resolve),
                },
                /*************************** 服务管理  *****************************/
                {   //留言管理
                    path: '/message/index',
                    name:'messageIndex',
                    component:(resolve) => require(['./views/service/message/MessageIndex.vue'],resolve),
                },
                {   //停水管理
                    path: '/nowater/index',
                    name:'nowaterIndex',
                    component:(resolve) => require(['./views/service/nowater/NowaterIndex.vue'],resolve),
                },
                //停水管理编辑
                {
                    path: '/nowater/add/:id',
                    name:'nowaterEdit',
                    component:(resolve) => require(['./views/service/nowater/NowaterAdd.vue'],resolve),
                },
                //停水管理新增
                {
                    path: '/nowater/add',
                    name:'nowaterAdd',
                    component:(resolve) => require(['./views/service/nowater/NowaterAdd.vue'],resolve),
                },
                {   //报装管理
                    path: '/applyinstall/index',
                    name:'applyinstallIndex',
                    component:(resolve) => require(['./views/service/applyinstall/ApplyinstallIndex.vue'],resolve),
                },
                /*************************** 系统消息  *****************************/
                {   //消息管理
                    path: '/news/index',
                    name:'newsIndex',
                    component:(resolve) => require(['./views/news/NewsIndex.vue'],resolve),
                    children:[
                        {   //全部消息
                            path: '/allnews/index',
                            name:'allNewsIndex',
                            component:(resolve) => require(['./views/news/AllNewsIndex.vue'],resolve),
                        },
                        {   //工单消息
                            path: '/ordernews/index',
                            name:'orderNewsIndex',
                            component:(resolve) => require(['./views/news/OrderNewsIndex.vue'],resolve),
                        },
                        {   //巡检消息
                            path: '/inspectionnews/index',
                            name:'InspectionNewsIndex',
                            component:(resolve) => require(['./views/news/InspectionNewsIndex.vue'],resolve),
                        },
                        {   //留言消息
                            path: '/messagenews/index',
                            name:'MessageNewsIndex',
                            component:(resolve) => require(['./views/news/MessageNewsIndex.vue'],resolve),
                        },
                        {   //报装消息
                            path: '/installnews/index',
                            name:'InstallNewsIndex',
                            component:(resolve) => require(['./views/news/InstallNewsIndex.vue'],resolve),
                        },
                        {   //报修消息
                            path: '/repairnews/index',
                            name:'RepairNewsIndex',
                            component:(resolve) => require(['./views/news/RepairNewsIndex.vue'],resolve),
                        },
                        {   //全部消息详情
                            path: '/news/Detail/:id',
                            name:'newsDetail',
                            component:(resolve) => require(['./views/news/NewsDetail.vue'],resolve),
                        }
                    ]
                },
            ]
        },
    ]
});

export default router;
