import Vue from 'vue'
import Router from 'vue-router'
import store from '../../vuex/store'

Vue.use(Router);

const vm = new Vue({
    store
});

const router = new Router({
    routes: [
        /**
         * 后台主页
         */
        {
            path: '/',
            redirect: '/type/index',
        },
        /**
         * 登录
         */
        {
            path: '/login',
            name: 'login',
            component:(resolve) => require(['./views/login/Login.vue'],resolve),
        },

        {
            path: '/index',
            component:(resolve) => require(['./views/index/Index.vue'],resolve),
            children:[
                {
                    path: '/type/index',
                    name:'工程类型',
                    component:(resolve) => require(['./views/type/typeIndex.vue'],resolve),
                    children:[
                        {
                            path: '/type/index',
                            name:'typeIndex',
                            component:(resolve) => require(['./views/type/QuanJingTuIndex.vue'],resolve),
                        },
                        {
                            path: '/type/project',
                            name:'typeProject',
                            component:(resolve) => require(['./views/type/ProjectIndex.vue'],resolve),
                        },{
                            path: '/type/zutaitu',
                            name:'typeZutaitu',
                            component:(resolve) => require(['./views/type/ZuTaTuIndex.vue'],resolve),
                        },

                    ]
                },
                {
                    path: '/equipment/index',
                    name:'设备管理',
                    component:(resolve) => require(['./views/equipment/equipmentIndex.vue'],resolve),
                    children:[
                        {
                            path: '/equipment/index',
                            name:'equipmentIndex',
                            component:(resolve) => require(['./views/equipment/GatewayType.vue'],resolve),
                        },
                        {
                            path: '/equipment/gatewayconfig',
                            name:'gatewayConfig',
                            component:(resolve) => require(['./views/equipment/GatewayConfig.vue'],resolve),
                        },
                        {
                            path: '/equipment/intelligentcontrol',
                            name:'intelligentControl',
                            component:(resolve) => require(['./views/equipment/IntelligentControl.vue'],resolve),
                        },

                    ]
                },
                /**
                 * 运维知识库
                 */
                {
                    path: '/scheme/index',
                    name: 'schemeIndex',
                    component:(resolve) => require(['./views/scheme/SchemeIndex.vue'],resolve),
                },
                {
                    path: '/scheme/add',
                    name: 'schemeAdd',
                    component:(resolve) => require(['./views/scheme/SchemeAdd.vue'],resolve),
                },
                //运维知识库编辑
                {
                    path: '/scheme/edit/:id',
                    name:'schemeEdit',
                    component:(resolve) => require(['./views/scheme/SchemeAdd.vue'],resolve),
                },
                {
                    path: '/dev/type/index',
                    name:'设备管理',
                    component:(resolve) => require(['./views/device/DeviceIndex.vue'],resolve),
                    children:[
                        {
                            path: '/dev/type/index',
                            name:'设备类型',
                            component:(resolve) => require(['./views/device/DeviceTypeIndex.vue'],resolve),
                        },
                        {
                            path: '/dev/info/index',
                            name:'设备信息',
                            component:(resolve) => require(['./views/device/DeviceInfoIndex.vue'],resolve),
                        },
                    ]
                },
                {
                    path: '/project/index',
                    name:'工程管理',
                    component:(resolve) => require(['./views/project/ProjectIndex.vue'],resolve),
                    children:[
                        {
                            path: '/project/index',
                            name:'工程管理',
                            component:(resolve) => require(['./views/project/ProjectBaseInfoIndex.vue'],resolve),
                        },
                        {
                            path: '/person/index/:id',
                            name:'工程和人员关联信息',
                            component:(resolve) => require(['./views/project/person/PersonIndex.vue'],resolve),
                        },
                        {
                            path: '/business/index/:id',
                            name:'工程业务信息',
                            component:(resolve) => require(['./views/project/business/BusinessIndex.vue'],resolve),
                        },
                        {
                            path: '/images/index/:id',
                            name:'图片配置',
                            component:(resolve) => require(['./views/project/images/ImagesIndex.vue'],resolve),
                        },
                        {
                            path: '/quanjingtu/index/:id',
                            name:'全景图配置',
                            component:(resolve) => require(['./views/project/qujingtu/QuanJingTuIndex.vue'],resolve),
                        },
                        {
                            path: '/video/index/:id',
                            name:'VideoIndex',
                            component:(resolve) => require(['./views/project/video/VideoIndex.vue'],resolve),
                        }
                    ]
                },

                {
                    path: '/country/index',
                    name:'乡镇管理',
                    component:(resolve) => require(['./views/country/CountryIndex.vue'],resolve),
                },
                //数据分析
                {
                    path: '/data/index',
                    name:'dataIndex',
                    component:(resolve) => require(['./views/data/DataIndex.vue'],resolve),
                },
                //警情分析
                {
                    path: '/alarm/index',
                    name:'alarmIndex',
                    component:(resolve) => require(['./views/alarm/AlarmIndex.vue'],resolve),
                },
                {
                    name:'user',                 //用户管理
                    path:'/user',
                    meta: {
                        name:'用户管理',
                    },
