                    path:'/menu',
                    meta: {
                        name:'菜单管理',
                    },
                    component:(resolve) => require(['./views/system/menu/menu.vue'],resolve)
                },
                {
                    name:'ad',                 //行政区划管理
                    path:'/ad',
                    meta: {
                        name:'行政区划管理',
                    },
                    component:(resolve) => require(['./views/system/administrative/administrative.vue'],resolve)
                },
                {
                    name:'post',                 //岗位管理
                    path:'/post/index',
                    meta: {
                        name:'岗位管理',
                    },
                    component:(resolve) => require(['./views/system/post/PostIndex.vue'],resolve)
                },
                {
                    name:'instite',                 //岗位管理
                    path:'/instite/index',
                    meta: {
                        name:'组织机构',
                    },
                    component:(resolve) => require(['./views/system/institution/InstiteIndex.vue'],resolve)
                },
                {
                    name:'formula',                 //定时参数
                    path:'/formula/index',
                    meta: {
                        name:'定时参数',
                    },
                    component:(resolve) => require(['./views/params/formula/FormulaIndex.vue'],resolve)
                },
                {
                    name:'timing',                 //定时参数
                    path:'/timing/index',
                    meta: {
                        name:'公式参数',
                    },
                    component:(resolve) => require(['./views/params/timing/TimingIndex.vue'],resolve)
                },
            ]
        },
        /*************************************************************
         *                                                           *
         *                          组态图                             *
         *                                                           *
         *************************************************************/
        {
            path: '/design/chart/one/:id/:code',    //ID：0为编辑状态；1为展示状态
            name: 'login',
            component:(resolve) => require(['./views/zutaitu/designChartOne.vue'],resolve),
        },
        {
            path: '/design/chart/two/:id/:code',    //ID：0为编辑状态；1为展示状态
            name: 'login',
            component:(resolve) => require(['./views/zutaitu/DesignChartTwo.vue'],resolve),
        },
        {
            path: '/design/chart/three/:id/:code',    //ID：0为编辑状态；1为展示状态
            name: 'login',
            component:(resolve) => require(['./views/zutaitu/DesignChartThree.vue'],resolve),
        },
        {
            path: '/design/chart/four/:id/:code',    //ID：0为编辑状态；1为展示状态
            name: 'login',
            component:(resolve) => require(['./views/zutaitu/DesignChartFour.vue'],resolve),
        },
        {
            path: '/design/chart/five/:id/:code',    //ID：0为编辑状态；1为展示状态
            name: 'login',
            component:(resolve) => require(['./views/zutaitu/DesignChartFive.vue'],resolve),
        },
        {
             path: '/design/chart/six/:id/:code',    //ID：0为编辑状态；1为展示状态
             name: 'login',
             component:(resolve) => require(['./views/zutaitu/DesignChartSix.vue'],resolve),
        },
        {
            path: '/design/chart/seven/:id/:code',    //ID：0为编辑状态；1为展示状态
            name: 'login',
            component:(resolve) => require(['./views/zutaitu/DesignChartSeven.vue'],resolve),
        },
        {
            path: '/design/chart/eight/:id/:code',    //ID：0为编辑状态；1为展示状态
            name: 'login',
            component:(resolve) => require(['./views/zutaitu/DesignChartEight.vue'],resolve),
        },
    ]
});

export default router;
