var cookieUtil = {
  set: function set(name, value, expires, path, domain, secure) {
    //设置cookie
    var cookieText = encodeURIComponent(name) + '=' + encodeURIComponent(value);

    if (expires) {
      var exp = new Date();
      exp.setTime(exp.getTime() + expires);
      cookieText += ';expires=' + exp.toGMTString();
    }

    if (path) {
      cookieText += ';path=' + path;
    }

    if (domain) {
      cookieText += ';domain=' + domain;
    }

    if (secure) {
      cookieText += ';secure';
    }

    document.cookie = cookieText;
  },
  get: function get(name) {
    //读取cookie
    var cookieName = encodeURIComponent(name) + '=',
        cookieStart = document.cookie.indexOf(cookieName),
        cookieValue = null;

    if (cookieStart > -1) {
      var cookieEnd = document.cookie.indexOf(';', cookieStart);

      if (cookieEnd == -1) {
        cookieEnd = document.cookie.length;
      }

      cookieValue = decodeURIComponent(document.cookie.substring(cookieStart + cookieName.length, cookieEnd));
    }

    return cookieValue;
  },
  unset: function unset(name, path, domain, secure) {
    this.set(name, '', new Date(0), domain, path);
  },
  setCookieDate: function setCookieDate(day) {
    //设置过期时间
    var date = null;

    if (typeof day == 'number' && day > 0) {
      date = new Date();
      date.setDate(date.getDate() + day);
    } else {
      throw new Error('!!');
    }

    return date;
  },
  delCookie: function delCookie(name) {
    var exp = new Date();
    exp.setTime(exp.getTime() + -1 * 24 * 60 * 60 * 1000);
    var cval = this.get(name);
    document.cookie = name + "=" + cval + "; expires=" + exp.toGMTString();
  }
};
export default cookieUtil;
