import Vue from 'vue';
var vm = new Vue();
export default {
  /**
   * 倒计时效果  disabled:button置灰    value:button值
   */
  countDownFunc: function countDownFunc(disabled, valuecode) {
    var s = 60;
    disabled = true;
    var timer = setInterval(function () {
      if (s > -1) {
        valuecode = "\u518D\u6B21\u53D1\u9001(".concat(s, ")s");
      } else {
        valuecode = '获取验证码';
        disabled = false;
        clearInterval(timer);
      }

      s--;
    }, 1000);
  }
};
